import React, { useState, useEffect } from 'react';
import { BookOpen, Users, Clock, X, CheckCircle } from 'lucide-react';
import { Card, CardHeader, CardTitle, CardDescription, CardContent } from '../UI/Card';
import Button from '../UI/Button';
import Badge from '../UI/Badge';
import { libraryAPI } from '../../services/api';

const LibrarySection = () => {
  const [libraries, setLibraries] = useState([]);
  const [selectedLibrary, setSelectedLibrary] = useState(null);
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [userBookings, setUserBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [bookingLoading, setBookingLoading] = useState(false);

  useEffect(() => {
    fetchLibraries();
    fetchBookings();
  }, []);

  const fetchLibraries = async () => {
    try {
      setError(null);
      const data = await libraryAPI.getLibraries();
      setLibraries(data);
    } catch (error) {
      setError('Failed to fetch libraries');
      console.error('Error fetching libraries:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchBookings = async () => {
    try {
      const bookings = await libraryAPI.getBookings();
      setUserBookings(bookings.filter(b => b.type === 'library'));
    } catch (error) {
      console.error('Error fetching bookings:', error);
    }
  };

  const handleLibrarySelect = (libraryId) => {
    setSelectedLibrary(libraryId);
    setSelectedSeat(null);
    setError(null);
  };

  const handleSeatSelect = (seatNumber) => {
    const library = libraries.find(l => l._id === selectedLibrary);
    const seat = library?.seats.find(s => s.number === seatNumber);
    
    if (seat && !seat.isOccupied) {
      setSelectedSeat(seatNumber);
    }
  };

  const handleBookSeat = async () => {
    if (selectedLibrary && selectedSeat) {
      setBookingLoading(true);
      try {
        await libraryAPI.bookSeat(selectedLibrary, selectedSeat);
        await fetchLibraries();
        await fetchBookings();
        setSelectedSeat(null);
        setError(null);
      } catch (error) {
        setError(error.response?.data?.message || 'Failed to book seat');
      } finally {
        setBookingLoading(false);
      }
    }
  };

  const handleLeaveSeat = async (bookingId) => {
    try {
      await libraryAPI.leaveSeat(bookingId);
      await fetchLibraries();
      await fetchBookings();
      setError(null);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to leave seat');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading libraries...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {error && (
        <div className="p-4 bg-red-100 border border-red-400 text-red-700 rounded-lg flex items-center justify-between">
          <span>{error}</span>
          <button onClick={() => setError(null)} className="text-red-500 hover:text-red-700">
            <X className="h-4 w-4" />
          </button>
        </div>
      )}

      {/* Current Bookings */}
      {userBookings.length > 0 && (
        <Card className="border-green-200 bg-green-50">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2 text-green-800">
              <CheckCircle className="h-5 w-5" />
              <span>My Current Booking</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {userBookings.map((booking) => (
                <div key={booking._id} className="flex items-center justify-between p-4 bg-white rounded-lg shadow-sm">
                  <div className="space-y-1">
                    <p className="font-medium text-green-800">{booking.library?.name}</p>
                    <p className="text-sm text-gray-600">
                      Seat {booking.seatNumber} • {booking.library?.floor}
                    </p>
                    <p className="text-xs text-gray-500">
                      Booked at {new Date(booking.bookedAt).toLocaleString()}
                    </p>
                  </div>
                  <Button 
                    variant="outline" 
                    onClick={() => handleLeaveSeat(booking._id)}
                    className="text-red-600 border-red-300 hover:bg-red-50"
                  >
                    Leave Seat
                  </Button>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Library List */}
        <div className="space-y-4">
          <h3 className="text-lg font-semibold">Available Libraries</h3>
          {libraries.length === 0 ? (
            <Card>
              <CardContent className="pt-6 text-center">
                <p className="text-gray-500">No libraries available</p>
              </CardContent>
            </Card>
          ) : (
            libraries.map((library) => {
              const availableSeats = library.seats.filter(seat => !seat.isOccupied).length;
              const occupancyRate = ((library.totalSeats - availableSeats) / library.totalSeats * 100).toFixed(0);
              
              return (
                <Card 
                  key={library._id} 
                  className={`cursor-pointer transition-all ${
                    selectedLibrary === library._id ? 'ring-2 ring-blue-500 shadow-md' : 'hover:shadow-md'
                  }`}
                  onClick={() => handleLibrarySelect(library._id)}
                >
                  <CardHeader>
                    <CardTitle className="flex items-center justify-between">
                      <span>{library.name}</span>
                      <Badge variant={availableSeats > 10 ? "default" : availableSeats > 5 ? "secondary" : "destructive"}>
                        {availableSeats} available
                      </Badge>
                    </CardTitle>
                    <CardDescription>{library.floor}</CardDescription>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      <div className="flex items-center justify-between text-sm">
                        <div className="flex items-center space-x-2">
                          <Users className="h-4 w-4" />
                          <span>Total Seats: {library.totalSeats}</span>
                        </div>
                        <span className="text-gray-600">{occupancyRate}% occupied</span>
                      </div>
                      
                      {/* Occupancy Bar */}
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className={`h-2 rounded-full transition-all ${
                            occupancyRate < 50 ? 'bg-green-500' : 
                            occupancyRate < 80 ? 'bg-yellow-500' : 'bg-red-500'
                          }`}
                          style={{ width: `${occupancyRate}%` }}
                        ></div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })
          )}
        </div>

        {/* Seat Selection */}
        <div>
          {selectedLibrary ? (
            <Card>
              <CardHeader>
                <CardTitle>Select Your Seat</CardTitle>
                <CardDescription>
                  {libraries.find(l => l._id === selectedLibrary)?.name}
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {/* Seat Grid */}
                  <div className="grid grid-cols-10 gap-2">
                    {libraries.find(l => l._id === selectedLibrary)?.seats.map((seat) => (
                      <div
                        key={seat.number}
                        onClick={() => handleSeatSelect(seat.number)}
                        className={`
                          w-8 h-8 rounded text-xs flex items-center justify-center cursor-pointer transition-all font-medium
                          ${seat.isOccupied 
                            ? 'bg-red-500 text-white cursor-not-allowed' 
                            : selectedSeat === seat.number 
                              ? 'bg-blue-500 text-white shadow-lg' 
                              : 'bg-gray-200 hover:bg-gray-300 text-gray-700'
                          }
                        `}
                        title={seat.isOccupied ? 'Occupied' : `Seat ${seat.number}`}
                      >
                        {seat.number}
                      </div>
                    ))}
                  </div>

                  {/* Legend */}
                  <div className="flex justify-center space-x-6 text-xs">
                    <div className="flex items-center space-x-1">
                      <div className="w-4 h-4 bg-gray-200 rounded"></div>
                      <span>Available</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <div className="w-4 h-4 bg-blue-500 rounded"></div>
                      <span>Selected</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <div className="w-4 h-4 bg-red-500 rounded"></div>
                      <span>Occupied</span>
                    </div>
                  </div>

                  {/* Book Button */}
                  {selectedSeat && (
                    <div className="p-4 bg-blue-50 rounded-lg">
                      <div className="flex items-center justify-between">
                        <div>
                          <p className="font-medium">Seat {selectedSeat}</p>
                          <p className="text-sm text-blue-600">
                            {libraries.find(l => l._id === selectedLibrary)?.name}
                          </p>
                        </div>
                        <Button 
                          onClick={handleBookSeat}
                          disabled={bookingLoading}
                        >
                          {bookingLoading ? 'Booking...' : 'Book Seat'}
                        </Button>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          ) : (
            <Card>
              <CardContent className="pt-6 text-center">
                <BookOpen className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500">Select a library to view available seats</p>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
};

export default LibrarySection;
